function PreviewCard({setDisplay, GreetingImage, Greeting, Body, Closing}) {

    const backToCreate = () => {
        setDisplay("create");
    }
    return (
        <div id="PreviewCard">
            <div className="img-wrap">
				<img src={GreetingImage} alt="" />
			</div>
            <div className="message-wrap">
                <p className="greeting-wrap">
                    {Greeting},
                </p>
                <p className="body-wrap">
                    {Body}
                </p>
                <p className="closing-wrap">
                    {Closing}
                </p>
            </div>
            <div id="button-row">
                <div className="btn-wrap">
                    <button onClick={backToCreate}>Back to Create</button>
                </div>
                <div className="btn-wrap">
                    <button>button</button>
                </div>
            </div>
        </div>
    );
}

export default PreviewCard;
